import React, { Component } from 'react'


export default class Identity extends Component {

  constructor(props) {
    super(props);
    this.state = {
      name: "",
      surname: ""
    };
  }

 /* const saveName = () =>{
    this.setState({
      name: 
    })
  }*/

   registerChange (e){
    console.log(e.target.value)
    console.log(this.state.name)
    this.setState({
      name: e.target.value
    })
  }

  render() {
    return (
      <div className='identity'>
          <div className='firstnameInput'>
          <label for="firstname">First name</label>
          <input onChange={this.registerChange} type="text" id="firstname" placeholder="Your first name" required/>
          <button id='saveFirstNameBtn' type='button'>Save</button>
          <p className='nameVisualizer'>{this.state.name}</p>
          </div>

          <div className='lastnameInput'>
          <label for="lastname">Last name</label>
          <input type="text" id="lastname" placeholder="Your last name" required/>
          <button id="saveLastNameBtn" type='button'>Save</button>
          <p className='lastNameVisualizer'>{this.state.surname}</p>
          </div>


      </div>
    )
  }
}
