import React, { Component } from 'react'

export default class Work extends Component {
  constructor(props) {
    super(props);
    this.state = {
      workedas: "",
      startdate: "",
      finishdate: "",
    };

  }

  registerWorkedAsChange = (e) =>{
    console.log(e.target.value)
    this.setState({
      workedas: e.target.value
    })
  }

  registerStartDateChange = (e) =>{
    console.log(e.target.value)
    this.setState({
      startdate: e.target.value
    })
  }

  registerFinishDateChange = (e) =>{
    console.log(e.target.value)
    this.setState({
      finishdate: e.target.value
    })
  }

  saveWork = (e) =>{
    const workedAsVisualizer = e.target.parentElement.parentElement.nextSibling.firstChild
    const startDateVisualizer = e.target.parentElement.parentElement.nextSibling.firstChild.nextSibling
    const finishDateVisualizer = e.target.parentElement.parentElement.nextSibling.firstChild.nextSibling.nextSibling
  console.log(workedAsVisualizer, startDateVisualizer, finishDateVisualizer)
  if (this.state.workedas !== "" && this.state.startdate !== "" && this.state.finishdate !== ""){
    workedAsVisualizer.textContent = this.state.workedas
    workedAsVisualizer.style.cssText = "color:black"
    startDateVisualizer.textContent = "Start date: " + this.state.startdate
    startDateVisualizer.style.cssText = "color:black"
    finishDateVisualizer.textContent = "Finish date: " + this.state.finishdate
    finishDateVisualizer.style.cssText = "color:black"
  } else if (this.state.workedas === "" && this.state.startdate !== "" && this.state.finishdate !== ""){
    workedAsVisualizer.textContent = "Your work description is missing"
    workedAsVisualizer.style.cssText = "color: red"
    startDateVisualizer.textContent = ""
    finishDateVisualizer.textContent = ""
  } else if (this.state.workedas === "" && this.state.startdate === "" && this.state.finishdate !== ""){
    workedAsVisualizer.textContent = "Your work description is missing"
    workedAsVisualizer.style.cssText = "color: red"
    startDateVisualizer.textContent = "The start date of your work experience is missing"
    startDateVisualizer.style.cssText = "color: red"
    finishDateVisualizer.textContent = ""
  } else if (this.state.workedas === "" && this.state.startdate === "" && this.state.finishdate === ""){
    workedAsVisualizer.textContent = "Your work description is missing"
    workedAsVisualizer.style.cssText = "color: red"
    startDateVisualizer.textContent = "The start date of your work experience is missing"
    startDateVisualizer.style.cssText = "color: red"
    finishDateVisualizer.textContent = "The finish date of your education is missing"
    finishDateVisualizer.style.cssText = "color: red"
  } else if (this.state.workedas !== "" && this.state.startdate === "" && this.state.finishdate === ""){
    workedAsVisualizer.textContent = ""
    startDateVisualizer.textContent = "The start date of your work experience is missing"
    startDateVisualizer.style.cssText = "color: red"
    finishDateVisualizer.textContent = "The finish date of your education is missing"
    finishDateVisualizer.style.cssText = "color: red"
  } else if (this.state.workedas === "" && this.state.startdate !== "" && this.state.finishdate === ""){
    startDateVisualizer.textContent = ""
    workedAsVisualizer.textContent = "Your work description is missing"
    workedAsVisualizer.style.cssText = "color: red"
    finishDateVisualizer.textContent = "The finish date of your education is missing"
    finishDateVisualizer.style.cssText = "color: red"
  } 
}





  render() {
    return (
      <div className='workExperience'>
        <div className='workForm'>

          <div className='workedasDiv'>
          <label htmlFor='workedas'>Worked as:</label>
          <input value={this.state.workedas} onChange={this.registerWorkedAsChange} type="text" id="workedas" required/>
          </div>

          <div className='startDateDiv'>
          <label htmlFor='startDate'>Start date:</label>
          <input value={this.state.startdate} onChange={this.registerStartDateChange} type="date" id="startDate" required/>
          </div>

         <div className='finishDateDiv'>
         <label htmlFor='finishDate'>Finish date:</label>
          <input value={this.state.finishdate} onChange={this.registerFinishDateChange} type="date" id="finishDate" required/>
         </div>

         <div className='submitButtonDiv'>
           <button onClick={this.saveWork} type='button' id='submitWorkBtn'>Save</button>
         </div>

        </div>
        <div className='workExperienceVisualizer'>
          <h2></h2>
          <p></p>
          <p></p>
        </div>
      </div>
    )
  }
}
