import React, { Component } from 'react'

export default class Education extends Component {

  constructor(props) {
    super(props);
    this.state = {
      schoolname: "",
      startdate: "",
      finishdate: "",
    };

  }

  registerSchoolChange = (e) =>{
    console.log(e.target.value)
    this.setState({
      schoolname: e.target.value
    })
  }

  registerStartDateChange = (e) =>{
    console.log(e.target.value)
    this.setState({
      startdate: e.target.value
    })
  }

  registerFinishDateChange = (e) =>{
    console.log(e.target.value)
    this.setState({
      finishdate: e.target.value
    })
  }

  saveEducation = (e) =>{
    const schoolNameVisualizer = e.target.parentElement.parentElement.nextSibling.firstChild
    const startDateVisualizer = e.target.parentElement.parentElement.nextSibling.firstChild.nextSibling
    const finishDateVisualizer = e.target.parentElement.parentElement.nextSibling.firstChild.nextSibling.nextSibling
  console.log(schoolNameVisualizer, startDateVisualizer, finishDateVisualizer)
  schoolNameVisualizer.textContent = this.state.schoolname
  startDateVisualizer.textContent = this.state.startdate
  finishDateVisualizer.textContent = this.state.finishdate

  }

  render() {
    return (
      <div className='education'>
        <div className='educationForm'>

          <div className='schoolNameDiv'>
          <label htmlFor='schoolName'>School name:</label>
          <input value={this.state.schoolname} onChange={this.registerSchoolChange} type="text" id="schoolName" required/>
          </div>

          <div className='startDateDiv'>
          <label htmlFor='startDate'>Start date:</label>
          <input value={this.state.startdate} onChange={this.registerStartDateChange} type="date" id="startDate" required/>
          </div>

         <div className='finishDateDiv'>
         <label htmlFor='finishDate'>Finish date:</label>
          <input value={this.state.finishdate} onChange={this.registerFinishDateChange} type="date" id="finishDate" required/>
         </div>

         <div className='submitButtonDiv'>
           <button onClick={this.saveEducation} type='button' id='submitEducationBtn'>Save</button>
         </div>

        </div>
        <div className='educationVisualizer'>
          <h2></h2>
          <p></p>
          <p></p>
        </div>
      </div>
    )
  }
}
